package CoreJava;

public class NestedLoopsInvertednumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int k=1;
		
		for (int i =1; i <=4; i++) {
			
			//int k=1;
			
			for (int j =i ; j >0; j--) 
			{
				
				System.out.print(k);
				System.out.print("\t");
				k++;
				
				}
			System.out.println(" ");
			}
			
		}
		
	}


